using RestEase;
using System.Collections;
using System.Text;

namespace {{ namespace }}
{
    internal static class OpenApiSdkGeneratorUtils
    {
        public static string GetRawQueryString(this object obj)
        {
            var type = obj.GetType();
            var sb = new StringBuilder();
            var isFirst = true;
            foreach (var property in type.GetProperties())
            {
                var queryAttribute = property.GetCustomAttributes(true)
                    .OfType<QueryAttribute>().FirstOrDefault();

                if (queryAttribute == null)
                {
                    continue;
                }

                var queryName = queryAttribute.Name ?? property.Name;
                if (!property.PropertyType.IsEnumerable())
                {
                    sb.Append($"{(isFirst ? string.Empty : "&")}{queryName}={property.GetValue(obj)}");
                    isFirst = false;
                    continue;
                }

                if (!property.PropertyType.IsGenericEnumerable())
                {
                    var array = property.GetValue(obj) as Array;

                    sb.AppendArray(queryName, array, isFirst);
                    isFirst = false;
                    continue;
                }

                var enumerable = (IEnumerable?)property.GetValue(obj);
                sb.AppendIEnumerable(queryName, enumerable, isFirst);

                isFirst = false;
            }

            return sb.ToString();
        }

        public static bool IsEnumerable(this Type type)
        {
            return type.IsGenericEnumerable() || type.IsArray;
        }

        private static bool IsGenericEnumerable(this Type type)
        {
            const string IENUMERABLE = "IEnumerable";

            return type.IsGenericType &&
                type.GetInterfaces().Any(
                ti => (ti == typeof(IEnumerable<>) || ti.Name == IENUMERABLE));
        }

        private static void AppendArray(this StringBuilder sb, string queryName, Array? array, bool isFirst)
        {
            if (array == null)
            {
                return;
            }

            foreach (var item in array)
            {
                sb.Append($"{(isFirst ? string.Empty : "&")}{queryName}={item.ToString()}");
            }
        }

        private static void AppendIEnumerable(this StringBuilder sb, string queryName, IEnumerable? enumerable, bool isFirst)
        {
            if (enumerable == null)
            {
                return;
            }

            foreach (var item in enumerable)
            {
                sb.Append($"{(isFirst ? string.Empty : "&")}{queryName}={item.ToString()}");
            }
        }
    }
}
